import Request from '../helpers/Request';

class FullArticleContainer extends Component {
  constructor(props){
    super(props);
    this.state = {
      article: null
    }
  }
  componentDidMount(){
    let request = new Request();
    const url = '/api/articles' + this.props.id + '?projection=embedForArticle';
  }
}
