import React, {Component} from 'react';

const FullArticle = (props) => {
  if(!props.article){
    return null;
  }

  return (
    <div className="FullSingleArticle">
      <h1>{props.article.title}</h1>
      <h4>By {props.article.author}</h4>
      <p>{props.article.summary}</p>

    </div>
  )

}

export default FullArticle;
